import React, { useState, useEffect } from 'react';
import { Box, Container, CssBaseline, Typography, CircularProgress, TextField, Button, createTheme, ThemeProvider, AppBar, Toolbar, Tab, Tabs, useMediaQuery, Paper, Divider, Chip, Avatar } from '@mui/material';
import MapIcon from '@mui/icons-material/Map';
import VideoLibraryIcon from '@mui/icons-material/VideoLibrary';
import ExploreIcon from '@mui/icons-material/Explore';
import AccessTimeIcon from '@mui/icons-material/AccessTime';
import LocationOnIcon from '@mui/icons-material/LocationOn';
import Map from './components/Map';
import axios from 'axios';
import './App.css';
import ContentView from './components/ContentView';

// Create the custom theme with the gradient background
const theme = createTheme({
  palette: {
    primary: {
      main: '#1768ac',
    },
    secondary: {
      main: '#06bee1',
    },
    background: {
      default: '#ffffff',
    },
  },
  typography: {
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
    h4: {
      fontWeight: 600,
    },
  },
  components: {
    MuiCssBaseline: {
      styleOverrides: {
        body: {
          background: 'linear-gradient(135deg, #03256c 0%, #2541b2 25%, #1768ac 50%, #06bee1 75%, #ffffff 100%)',
          backgroundAttachment: 'fixed',
          minHeight: '100vh',
          overflow: 'hidden', // Prevent body scrolling
        },
      },
    },
  },
});

function App() {
  // Check if device is mobile
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  // Existing state
  const [locations, setLocations] = useState([]);
  const [filteredLocations, setFilteredLocations] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('restaurants');
  const [navValue, setNavValue] = useState(1);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // New state for itinerary functionality
  const [itineraryText, setItineraryText] = useState('');
  const [hours, setHours] = useState('');
  const [recommendations, setRecommendations] = useState([]);
  const [isSubmitting, setIsSubmitting] = useState(false);

  // API base URL
  const API_BASE_URL = 'http://localhost:5001';

  // Your existing useEffect for fetching locations
  useEffect(() => {
    setLoading(true);
    axios.get(`${API_BASE_URL}/api/places/all`)
      .then(response => {
        if (response.data.status === 'success') {
          const formattedLocations = response.data.data.map(place => ({
            id: place.id,
            name: place.title,
            category: place.location_type || 'all',
            address: place.place_name,
            position: place.position,
            description: place.description,
            media: place.media
          }));
          setLocations(formattedLocations);
        } else {
          setError('Failed to load locations');
        }
        setLoading(false);
      })
      .catch(err => {
        console.error('Error fetching locations:', err);
        setError('Failed to load locations');
        setLoading(false);
      });
  }, []);

  // Your existing useEffect for category filtering
  useEffect(() => {
    if (selectedCategory === 'all') {
      setFilteredLocations(locations);
    } else {
      setFilteredLocations(
        locations.filter(location => location.category === selectedCategory)
      );
    }
  }, [selectedCategory, locations]);

  const handleMarkerClick = (marker) => {
    console.log('Marker clicked:', marker);
  };

  const handleLocationSelect = (location) => {
    console.log('Location selected:', location);
  };

  // New handler for itinerary submission
  const handleItinerarySubmit = async (event) => {
    event.preventDefault();
    setIsSubmitting(true);
    try {
      const response = await axios.post(`${API_BASE_URL}/api/recommendations`, {
        inputString: itineraryText,
        hours: hours
      });
      if (response.data.status === 'success') {
        setRecommendations(response.data.data);
      }
    } catch (error) {
      console.error('Error fetching recommendations:', error);
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleTabChange = (event, newValue) => {
    setNavValue(newValue);
  };

  const renderContent = () => {
    if (loading) {
      return (
        <Box display="flex" justifyContent="center" alignItems="center" minHeight="50vh">
          <CircularProgress />
        </Box>
      );
    }

    if (error) {
      return (
        <Box display="flex" justifyContent="center" alignItems="center" minHeight="50vh">
          <Typography color="error">{error}</Typography>
        </Box>
      );
    }

    switch (navValue) {
      case 0:
        return <ContentView />;
      case 1:
        return <Map markers={filteredLocations} onMarkerClick={handleMarkerClick} />;
      case 2:
        return (
          <Box 
            sx={{
              height: 'calc(100vh - 130px)',
              overflow: 'hidden',
              display: 'flex',
              flexDirection: 'column'
            }}
          >
            {/* Modern Itinerary UI */}
            <Paper
              elevation={0}
              sx={{
                p: isMobile ? 2 : 3,
                backgroundColor: 'rgba(255, 255, 255, 0.9)',
                borderRadius: '16px',
                backdropFilter: 'blur(10px)',
                boxShadow: '0 8px 32px rgba(0, 0, 0, 0.12)',
                height: '100%',
                display: 'flex',
                flexDirection: 'column',
                overflow: 'hidden'
              }}
            >
              <Box sx={{ mb: 3, textAlign: 'center' }}>
                <Typography
                  variant="h5"
                  sx={{
                    fontWeight: 700,
                    background: 'linear-gradient(90deg, #03256c 0%, #1768ac 100%)',
                    WebkitBackgroundClip: 'text',
                    WebkitTextFillColor: 'transparent',
                    mb: 1
                  }}
                >
                  Plan Your Perfect Day
                </Typography>
                <Typography variant="body2" color="text.secondary" sx={{ maxWidth: '600px', mx: 'auto' }}>
                  Tell us what you're looking to do and how much time you have. We'll create a personalized itinerary just for you.
                </Typography>
              </Box>

              <form onSubmit={handleItinerarySubmit} style={{ width: '100%', display: 'flex', flexDirection: 'column', flex: recommendations.length > 0 ? '0 0 auto' : 1 }}>
                <Box sx={{ display: 'flex', flexDirection: isMobile ? 'column' : 'row', gap: 2, mb: 3 }}>
                  <TextField
                    label="What are you looking to do?"
                    placeholder="E.g., I want to explore local cuisine, visit museums, and do some shopping"
                    value={itineraryText}
                    onChange={(e) => setItineraryText(e.target.value)}
                    multiline
                    rows={3}
                    fullWidth
                    variant="outlined"
                    sx={{
                      flex: isMobile ? '1' : '3',
                      '& .MuiOutlinedInput-root': {
                        backgroundColor: 'white',
                        borderRadius: '12px',
                      }
                    }}
                  />

                  <Box sx={{ 
                    display: 'flex', 
                    flexDirection: 'column', 
                    justifyContent: 'space-between',
                    flex: isMobile ? '1' : '1',
                  }}>
                    <TextField
                      label="Hours available"
                      type="number"
                      value={hours}
                      onChange={(e) => setHours(e.target.value)}
                      variant="outlined"
                      InputProps={{
                        startAdornment: <AccessTimeIcon sx={{ mr: 1, color: 'action.active' }} />,
                      }}
                      sx={{
                        mb: 2,
                        '& .MuiOutlinedInput-root': {
                          backgroundColor: 'white',
                          borderRadius: '12px',
                        }
                      }}
                    />

                    <Button
                      type="submit"
                      variant="contained"
                      disabled={isSubmitting || !itineraryText || !hours}
                      sx={{
                        py: 1.5,
                        borderRadius: '12px',
                        fontWeight: 'bold',
                        background: 'linear-gradient(90deg, #03256c 0%, #1768ac 100%)',
                        boxShadow: '0 4px 15px rgba(23, 104, 172, 0.25)',
                        transition: 'all 0.3s ease',
                        '&:hover': {
                          background: 'linear-gradient(90deg, #02174a 0%, #105088 100%)',
                          boxShadow: '0 6px 20px rgba(23, 104, 172, 0.35)',
                          transform: 'translateY(-2px)'
                        }
                      }}
                    >
                      {isSubmitting ? <CircularProgress size={24} color="inherit" /> : 'Create Itinerary'}
                    </Button>
                  </Box>
                </Box>
              </form>

              {/* Recommendation Results */}
              {recommendations.length > 0 && (
                <Box sx={{ 
                  flex: 1, 
                  overflow: 'auto', 
                  mt: 2,
                  pr: 1,
                  '&::-webkit-scrollbar': {
                    width: '6px',
                    borderRadius: '3px',
                  },
                  '&::-webkit-scrollbar-track': {
                    background: 'rgba(0,0,0,0.05)',
                    borderRadius: '3px',
                  },
                  '&::-webkit-scrollbar-thumb': {
                    background: 'rgba(0,0,0,0.15)',
                    borderRadius: '3px',
                  }
                }}>
                  <Paper
                    elevation={0}
                    sx={{
                      p: isMobile ? 2 : 3,
                      backgroundColor: 'rgba(255, 255, 255, 0.9)',
                      borderRadius: '16px',
                      border: '1px solid rgba(0, 0, 0, 0.05)',
                    }}
                  >
                    <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                      <Avatar sx={{ 
                        bgcolor: '#1768ac', 
                        width: 40, 
                        height: 40, 
                        mr: 2,
                        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)'
                      }}>
                        <ExploreIcon />
                      </Avatar>
                      <Typography variant="h6" fontWeight="bold">Your Custom Itinerary</Typography>
                    </Box>

                    <Divider sx={{ mb: 2 }} />

                    <Box sx={{ display: 'flex', mb: 3 }}>
                      <Chip 
                        icon={<AccessTimeIcon />} 
                        label={`${hours} hours`} 
                        sx={{ mr: 1, fontWeight: 'medium' }} 
                      />
                      <Chip 
                        icon={<LocationOnIcon />} 
                        label="Local area" 
                        sx={{ fontWeight: 'medium' }} 
                      />
                    </Box>

                    <Box sx={{ mt: 1 }}>
                      {recommendations.map((rec, index) => (
                        <Box key={index} sx={{ 
                          position: 'relative',
                          mb: 2,
                          pl: 4,
                          pb: index < recommendations.length - 1 ? 2 : 0,
                          '&:before': {
                            content: '""',
                            position: 'absolute',
                            left: '12px',
                            top: '24px',
                            bottom: 0,
                            width: '2px',
                            background: index < recommendations.length - 1 ? 'rgba(3, 37, 108, 0.2)' : 'transparent',
                            zIndex: 1
                          }
                        }}>
                          <Box sx={{ 
                            position: 'absolute',
                            left: 0,
                            top: 0,
                            width: '24px',
                            height: '24px',
                            borderRadius: '50%',
                            backgroundColor: '#1768ac',
                            color: 'white',
                            display: 'flex',
                            alignItems: 'center',
                            justifyContent: 'center',
                            zIndex: 2,
                            boxShadow: '0 2px 4px rgba(0, 0, 0, 0.2)'
                          }}>
                            {index + 1}
                          </Box>
                          <Typography variant="body1" sx={{ 
                            background: 'rgba(255, 255, 255, 0.7)',
                            p: 2,
                            borderRadius: '12px',
                            boxShadow: '0 2px 8px rgba(0, 0, 0, 0.05)',
                            border: '1px solid rgba(0, 0, 0, 0.05)'
                          }}>
                            {rec}
                          </Typography>
                        </Box>
                      ))}
                    </Box>
                  </Paper>
                </Box>
              )}
            </Paper>
          </Box>
        );
      default:
        return <Map markers={filteredLocations} onMarkerClick={handleMarkerClick} />;
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Box sx={{ flexGrow: 1, height: '100vh', overflow: 'hidden' }}>
        {/* Modern header with logo and navigation */}
        <AppBar 
          position="static" 
          sx={{ 
            background: 'transparent',
            boxShadow: 'none',
            padding: isMobile ? '4px 0' : '8px 0'
          }}
        >
          <Toolbar 
            sx={{ 
              justifyContent: 'space-between',
              flexDirection: isMobile ? 'column' : 'row',
              gap: isMobile ? 1 : 0,
              padding: isMobile ? '8px 16px' : undefined
            }}
          >
            {/* Stylish logo */}
            <Box sx={{ display: 'flex', alignItems: 'center' }}>
              <Typography 
                variant="h4" 
                sx={{ 
                  fontWeight: 'bold',
                  background: 'linear-gradient(45deg, #ffffff 10%, #06bee1 90%)',
                  WebkitBackgroundClip: 'text',
                  WebkitTextFillColor: 'transparent',
                  textShadow: '0 2px 10px rgba(255,255,255,0.2)',
                  letterSpacing: '0.05em',
                  fontFamily: '"Segoe UI", "Roboto", "Helvetica", sans-serif',
                  fontSize: { xs: '1.8rem', sm: '2.5rem' }
                }}
              >
                ROAM.AI
              </Typography>
            </Box>

            {/* Tab navigation in header */}
            <Tabs 
              value={navValue} 
              onChange={handleTabChange}
              textColor="inherit"
              variant={isMobile ? "fullWidth" : "standard"}
              sx={{ 
                '& .MuiTabs-indicator': { 
                  backgroundColor: '#fff',
                  height: 3
                },
                '& .MuiTab-root': {
                  color: 'rgba(255, 255, 255, 0.7)',
                  minWidth: isMobile ? '10px' : 'auto',
                  padding: isMobile ? '6px 8px' : 'auto',
                  fontSize: isMobile ? '0.75rem' : undefined,
                  '&.Mui-selected': {
                    color: '#fff',
                    fontWeight: 'bold'
                  }
                },
                width: isMobile ? '100%' : 'auto'
              }}
            >
              <Tab 
                icon={<VideoLibraryIcon sx={{ fontSize: isMobile ? '1.2rem' : '1.5rem' }} />} 
                label="Content" 
                sx={{ minWidth: 'auto' }} 
              />
              <Tab 
                icon={<MapIcon sx={{ fontSize: isMobile ? '1.2rem' : '1.5rem' }} />} 
                label="Map" 
                sx={{ minWidth: 'auto' }} 
              />
              <Tab 
                icon={<ExploreIcon sx={{ fontSize: isMobile ? '1.2rem' : '1.5rem' }} />} 
                label="Itinerary" 
                sx={{ minWidth: 'auto' }} 
              />
            </Tabs>
          </Toolbar>
        </AppBar>

        {/* Main content container */}
        <Container 
          maxWidth={isMobile ? "sm" : "lg"} 
          sx={{ 
            mt: isMobile ? 1 : 2,
            px: isMobile ? 1 : 2,
            height: 'calc(100vh - 130px)',
            overflow: 'hidden'
          }}
        >
          {renderContent()}
        </Container>
      </Box>
    </ThemeProvider>
  );
}

export default App;
